import React, { Component } from "react";
class RadioInput extends Component {
  state = {};

  handleChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
    console.log(value);
  };
  render() {
    return (
      <div>
        <label htmlFor="teacher">Teacher</label>&nbsp;&nbsp;
        <input
          type="radio"
          id="teacher"
          name="selection"
          value="teacher"
          onChange={this.handleChange}
        />
        &nbsp;&nbsp;&nbsp;&nbsp;
        <label htmlFor="admin">Admin</label>
        <input
          type="radio"
          id="admin"
          name="selection"
          value="admin"
          onChange={this.handleChange}
        />
        &nbsp;&nbsp;&nbsp;&nbsp;
        <label htmlFor="student">Student</label>
        <input
          type="radio"
          id="student"
          name="selection"
          value="student"
          onChange={this.handleChange}
        />
        &nbsp;&nbsp;&nbsp;
        <label htmlFor="obecell">OBE Cell</label>
        <input
          type="radio"
          id="obecell"
          name="selection"
          value="obecell"
          onChange={this.handleChange}
        />
      </div>
    );
  }
}

export default RadioInput;
