import React, { Component } from "react";

class checking extends Component {
  handleRadioChange = (e) => {
    this.setState({ [this.state.selection]: e.target.value });
    console.log(this.state.selection);
  };

  render() {
    const { selection } = this.state;
    return (
      <div>
        <input
          name="selection"
          id="teacher"
          value="teacher"
          checked={selection === "teacher"}
          type="radio"
          onChange={this.handleRadioChange}
        />
        Teacher
        <input
          name="selection"
          id="student"
          value="student"
          checked={selection === "student"}
          type="radio"
          onChange={this.handleRadioChange}
        />
        Student
        <input
          name="selection"
          id="admin"
          value="admin"
          checked={selection === "admin"}
          type="radio"
          onChange={this.handleRadioChange}
        />
        Admin
        <input
          name="selection"
          id="obecell"
          value="obecell"
          checked={selection === "obecell"}
          type="radio"
          onChange={this.handleRadioChange}
        />
        OBE Cell
      </div>
    );
  }
}

export default checking;
