import React, { Component } from "react";
class RadioInput extends Component {
  state = {};

  handleChange = (e) => {
    const { name, value } = e.target;
    this.setState();
  };
  render() {
    return (
      <div>
        <label htmlFor="teacher">Teacher</label>
        <input
          type="radio"
          id="teacher"
          name="selection"
          value="teacher"
          onChange={this.handleChange}
        />
        <label htmlFor="admin">admin</label>
        <input
          type="radio"
          id="admin"
          name="selection"
          value="admin"
          onChange={this.handleChange}
        />
        <br></br>
        <label htmlFor="student">student</label>
        <input
          type="radio"
          id="student"
          name="selection"
          value="student"
          onChange={this.handleChange}
        />
        <label htmlFor="obecell">obecell</label>
        <input
          type="radio"
          id="obecell"
          name="selection"
          value="obecell"
          onChange={this.handleChange}
        />
      </div>
    );
  }
}

export default RadioInput;
